import React from 'react';
import {Container, Row, Form, FormControl, FormLabel, Button, FormGroup, Alert} from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
          username: "",
          password: "",
          showAlert: false,
          alertMessage: '',
          alertType: 'success'
      };
    }
    // handleChange = evt => {
    //   this.setState({
    //     [evt.target.username] : evt.target.value,
    //   })
    // }

    componentDidMount(){
      this.fetchAllRecords();
    }

    //Add Record
    addRecord = () => {
      alert('hello')
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json")

      var body = JSON.stringify({
        username: this.state.username,
        password: this.state.password
      })
      // const url = "http://localhost:8000/api/create"
      fetch("http://localhost:8000/api/create",{
        method: 'POST',
        headers: myHeaders,
        body: body
      }).then((Response) => {
        Response.json()
        .then((result) => {
          console.log(result);
          this.setState({
            username:'',
            password:'',
            showAlert: true,
            alertMessage:result.response,
            alertType: 'success'
          })
        }).catch((err)=>{
          console.log(err);
        })
      })
    }

    //fetch all records from users table
    fetchAllRecords = () => {
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json")
      fetch("http://localhost:8000/api/view",{
        method: 'GET',
        headers: myHeaders,
      }).then((Response) => {
        Response.json()
        .then((result) => {
          console.log("result", result);
        }).catch((err)=>{
          console.log("error",err);
        })
      }) 
    }

    render(){
      return (
        <div>
            <Container>
            {
              this.state.showAlert === true ? 
              <Alert
                variant={this.state.alertType}
                onClose={
                  () => {
                    this.setState({
                      showAlert: false
                    })
                  }
                }
                dismissible
              >
                  <Alert.Heading>{this.state.alertMessage}</Alert.Heading>
              </Alert> : null
            }
              {/*insert form */}
              <Row>
                <Form>
                  <FormGroup>
                    <FormLabel>Username</FormLabel>
                    <FormControl type= "text" name= "username" placeholder= "Enetr Username" value= {this.props.username} onChange={this.handleChange}></FormControl>
                  </FormGroup>
                  <FormGroup>
                    <FormLabel>Password</FormLabel>
                    <FormControl type= "password" name= "password" placeholder= "Enetr Password" value= {this.props.password} onChange={this.handleChange}></FormControl>
                  </FormGroup>
                  <Button onClick={this.addRecord}>Login</Button>
                </Form>
              </Row>
            </Container>
        </div>
      )
    }
  }

  export default App;
