import React from "react";
import { Container, Row, Form, FormGroup, FormControl, FormLabel, Button, Alert, Table } from "react-bootstrap";

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			username: "",
			password: "",
			records: [],
			showAlert: false,
			alertMsg: "",
			alertType: "success",
			id: "",
			update: false,
		};
	}

  handleChange = (evt) => {
		this.setState({
			[evt.target.name]: evt.target.value,
		});
	};

	componentWillMount() {
		this.fetchAllRecords();
	}

	// add a record
	addRecord = () => {
		var myHeaders = new Headers();
		myHeaders.append("Content-Type", "application/json");

		var body = JSON.stringify({ username: this.state.username, password: this.state.password });
		fetch("http://localhost:8000/api/create", {
			method: "POST",
			headers: myHeaders,
			body: body,
		})
			.then((response) => response.json())
			.then((result) => {
				console.log(result);
				this.setState({
					username: "",
					password: "",
					showAlert: true,
					alertMsg: result.response,
					alertType: "success",
				});
			});
	};

	// fetch All Records
	fetchAllRecords = () => {
		var headers = new Headers();
		headers.append("Content-Type", "application/json");
		fetch("http://localhost:8000/api/view", {
			method: "GET",
			headers: headers,
		})
			.then((response) => response.json())
			.then((result) => {
				console.log("result", result);
				this.setState({
					records: result.response,
				});
			})
			.catch((error) => console.log("error", error));
	};

	// view single data to edit
	editRecord = (id) => {
		fetch("http://localhost:8000/api/view/" + id, {
			method: "GET",
		})
			.then((response) => response.json())
			.then((result) => {
				console.log(result);
				this.setState({
					id: id,
					update: true,
					username: result.response[0].username,
					password: result.response[0].password,
				});
			})
			.catch((error) => console.log("error", error));
	};

	// update record
	updateRecord = () => {
		var myHeaders = new Headers();
		myHeaders.append("Content-Type", "application/json");

		var body = JSON.stringify({ id: this.state.id, username: this.state.username, password: this.state.password });
		fetch("http://localhost:8000/api/update", {
			method: "PUT",
			headers: myHeaders,
			body: body,
		})
			.then((response) => response.json())
			.then((result) => {
				this.setState({
					showAlert: true,
					alertMsg: result.response,
					alertType: "success",
					update: false,
					id: "",
					username: "",
					password: "",
				});
				this.fetchAllRecords();
			})
			.catch((error) => console.log("error", error));
	};

	// delete a record
	deleteRecord = (id) => {
		fetch("http://localhost:8000/api/delete/" + id, {
			method: "DELETE",
		})
			.then((response) => response.json())
			.then((result) => {
				this.setState({
					showAlert: true,
					alertMsg: result.response,
					alertType: "danger",
				});
				this.fetchAllRecords();
			})
			.catch((error) => console.log("error", error));
	};
	render() {
		return (
			<div>
				<Container>
					{this.state.showAlert === true ? (
						<Alert
							variant={this.state.alertType}
							onClose={() => {
								this.setState({
									showAlert: false,
								});
							}}
							dismissible
						>
							<Alert.Heading>{this.state.alertMsg}</Alert.Heading>
						</Alert>
					) : null}

					{/* All Records */}
					<Row>
						<Table striped bordered hover size="sm">
							<thead>
								<tr>
									<th>id</th>
									<th>username</th>
									<th>password</th>
									<th colSpan="2">Actions</th>
								</tr>
							</thead>
							<tbody>
								{this.state.records.map((record) => {
									return (
										<tr>
											<td>{record.id}</td>
											<td>{record.username}</td>
											<td>{record.password}</td>
											<td>
												<Button variant="info" onClick={() => this.editRecord(record.id)}>
													Edit
												</Button>
											</td>
											<td>
												<Button variant="danger" onClick={() => this.deleteRecord(record.id)}>
													Delete
												</Button>
											</td>
										</tr>
									);
								})}
							</tbody>
						</Table>
					</Row>

					{/* Insert Form */}
					<Row>
						<Form>
							<FormGroup>
								<FormLabel>Enter the username</FormLabel>
								<FormControl type="text" username="username" placeholder="Enter the username" onChange={this.handleChange} value={this.state.username}></FormControl>
							</FormGroup>
							<FormGroup>
								<FormLabel>Enter the password</FormLabel>
								<FormControl type="text" username="password" value={this.state.password} onChange={this.handleChange} placeholder="Enter the password"></FormControl>
							</FormGroup>

							{this.state.update === true ? <Button onClick={this.updateRecord}>update</Button> : <Button onClick={this.addRecord}>Login</Button>}
						</Form>
					</Row>
				</Container>
			</div>
		);
	}
}

export default App;
