import React from 'react';
import {Container, Row, Form, FormControl, FormLabel, Button, FormGroup} from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
          username: "",
          password: ""
      };
    }
    handleChange = evt => {
      this.setState({
        [evt.target.username] : evt.target.value,
      })
    }

    //Add Record

    addRecord = () => {
      alert('hello')
      // var myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json")

      var body = JSON.stringify({
        username: this.state.username,
        password: this.state.password
      })
      // const url = "http://localhost:8000/api/create"
      fetch("http://localhost:8000/api/create",{
        method: 'POST',
        headers: myHeaders,
        body: body
      }).then((Response) => {
        Response.json()
        .then((result) => {
          console.log(result);
        }).catch((err)=>{
          console.log(err);
        })
      })
    }
    render(){
      return (
        <div>
            <Container>
              {/*insert form */}
              <Row>
                <Form>
                  <FormGroup>
                    <FormLabel>Username</FormLabel>
                    <FormControl type= "text" name= "username" placeholder= "Enetr Username" value= {this.props.username} onChange={this.handleChange}></FormControl>
                  </FormGroup>
                  <FormGroup>
                    <FormLabel>Password</FormLabel>
                    <FormControl type= "password" name= "password" placeholder= "Enetr Password" value= {this.props.password} onChange={this.handleChange}></FormControl>
                  </FormGroup>
                  <Button onClick={this.addRecord}>Login</Button>
                </Form>
              </Row>
            </Container>
        </div>
      )
    }
  }

  export default App;
